/**
 * @class Common utilities
 * @name glMatrix
 */
var glMatrix = {};

// Configuration Constants
glMatrix.EPSILON = 0.000001;
glMatrix.ARRAY_TYPE = (typeof Float32Array !== 'undefined') ? Float32Array : Array;
glMatrix.RANDOM = Math.random;
glMatrix.ENABLE_SIMD = false;

// Capability detection
glMatrix.SIMD_AVAILABLE = (glMatrix.ARRAY_TYPE === Float32Array) && ('SIMD' in this);
glMatrix.USE_SIMD = glMatrix.ENABLE_SIMD && glMatrix.SIMD_AVAILABLE;

/**
 * Sets the type of array used when creating new vectors and matrices
 *
 * @param {Type} type Array type, such as Float32Array or Array
 */
glMatrix.setMatrixArrayType = function(type) {
    glMatrix.ARRAY_TYPE = type;
}

var degree = Math.PI / 180;

/**
* Convert Degree To Radian
*
* @param {Number} Angle in Degrees
*/
glMatrix.toRadian = function(a){
     return a * degree;
}

/**
 * Tests whether or not the arguments have approximately the same value, within an absolute
 * or relative tolerance of glMatrix.EPSILON (an absolute tolerance is used for values less 
 * than or equal to 1.0, and a relative tolerance is used for larger values)
 * 
 * @param {Number} a The first number to test.
 * @param {Number} b The second number to test.
 * @returns {Boolean} True if the numbers are approximately equal, false otherwise.
 */
glMatrix.equals = function(a, b) {
	return Math.abs(a - b) <= glMatrix.EPSILON*Math.max(1.0, Math.abs(a), Math.abs(b));
}

module.exports = glMatrix;

export { default as Geometry } from './geometry';

export { default as ConeGeometry } from './cone-geometry';
export { default as CubeGeometry } from './cube-geometry';
export { default as CylinderGeometry } from './cylinder-geometry';
export { default as IcoSphereGeometry } from './ico-sphere-geometry';
export { default as PlaneGeometry } from './plane-geometry';
export { default as SphereGeometry } from './sphere-geometry';
export { default as TruncatedConeGeometry } from './truncated-cone-geometry';

import { glMatrix } from 'gl-matrix';

glMatrix.debug = true;
glMatrix.printRowMajor = true;

export { glMatrix };

export function checkNumber(value) {
  return Number.isFinite(value);
}

export function configure(options) {
  if ('epsilon' in options) {
    glMatrix.EPSILON = options.epsilon;
  }

  if ('debug' in options) {
    glMatrix.debug = options.debug;
  }
}

export function formatValue(value) {
  var precision = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : glMatrix.precision || 4;

  return parseFloat(value.toPrecision(precision));
}

export function isArray(value) {
  return Array.isArray(value) || ArrayBuffer.isView(value) && value.length !== undefined;
}

export function clone(array) {
  return array.clone ? array.clone() : new Array(array);
}

function map(value, func) {
  if (isArray(value)) {
    var result = clone(value);
    for (var i = 0; i < result.length; ++i) {
      result[i] = func(result[i], i, result);
    }
    return result;
  }
  return func(value);
}

export function radians(degrees) {
  return map(degrees, function (degrees) {
    return degrees / 180 * Math.PI;
  });
}

export function degrees(radians) {
  return map(radians, function (radians) {
    return radians * 180 / Math.PI;
  });
}

export function sin(radians) {
  return map(radians, function (angle) {
    return Math.sin(angle);
  });
}

export function cos(radians) {
  return map(radians, function (angle) {
    return Math.cos(angle);
  });
}

export function tan(radians) {
  return map(radians, function (angle) {
    return Math.tan(angle);
  });
}

export function asin(radians) {
  return map(radians, function (angle) {
    return Math.asin(angle);
  });
}

export function acos(radians) {
  return map(radians, function (angle) {
    return Math.acos(angle);
  });
}

export function atan(radians) {
  return map(radians, function (angle) {
    return Math.atan(angle);
  });
}

export function clamp(value, min, max) {
  return Math.max(min, Math.min(max, value));
}

export function equals(a, b) {
  return Math.abs(a - b) <= glMatrix.EPSILON * Math.max(1.0, Math.abs(a), Math.abs(b));
}

